preProcessingSpecificationV2.value:
({ endpointParameters }) => {
  const preProcessingObject = {
    "AAVE/USD": { path: "getVWAP/AAVEUSD", parameters: {} },
    "AERO/USD": { path: "getVWAP/AEROUSD", parameters: {} },
    "ADA/USD": { path: "getVWAP/ADAUSD", parameters: {} },
  }
  return {
    endpointParameters: {
      path: preProcessingObject[endpointParameters.name].path,
      ...preProcessingObject[endpointParameters.name].parameters,
    },
  }
}

postProcessingSpecificationV2.value:
({ response, endpointParameters }) => {
  const postProcessingObject = {
    "AAVE/USD": "(response) => { return response.price; }",
    "AERO/USD": "(response) => { return response.price; }",
    "ADA/USD": "(response) => { return response.price; }",
  }
  const parser = eval(postProcessingObject[endpointParameters.name])
  return { response: parser(response) }
}
